INSERT INTO `t_reviews` (`id`, `type`, `content`, `user_id`, `create_time`, `is_del`, `book_id`, `parent_id`, `root_id`, `like`) VALUES (2, 1, '温简言最擅长见人说人话，见鬼说鬼话。\n某日，他突然被迫成为了梦魇直播间中的一位新手主播，真的会死那种。\n\n温简言：“……”\n我rnm\n\n某新人成为最受瞩目主播，原因竟然是太会坑人，而且还是坑人坑鬼无所不坑。\n\n这家伙迟早翻车！\n\n幸灾乐祸看热闹的观众越来越多，可怎么等也等不到那一天。\n\n不过……\n为什么这个直播间的任务越来越奇怪了呢？\n\n【高级主播支线任务：亲吻恶灵三十秒】\n【奖励：未知】\n\n【顶级主播主线任务：脱衣诱惑邪神三分钟】\n【奖励：未知】\n\n观众：哦豁，主播这是掌握了新的财富密码吗？快点快点，有什么是我们付费观众不能看的！\n\n顶级BOSS步步逼近，低声诱惑：\n“怎么样？炒cp吗？假戏真做那种。”\n\n温简言：“……”\n\n？？？\n\n对不起，我很有原则的，骗人不骗心的谢谢。\n——你不要过来啊！', 2, '2023-11-28 20:37:54', 0, 1, 1, 1, 0);
